import React, { useState, useEffect } from "react";
import PunkAPIService from "../services/punkapi";
import Pagination from "./Pagination";
import "./ResourceList.css";

const ResourceList = () => {
  const [resources, setResources] = useState([]); // les ressources données par le fetch api
  const [currentPage, setCurrentPage] = useState(1); // pour updater la page actuelle
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      const fetchedResources = await PunkAPIService.fetchResources(currentPage); // fetch les données en fonction de la page actuelle (20 resultats par page)
      setResources(fetchedResources);
      const totalResources = 325; // Nombre total des bières
      const resourcesPerPage = 20; // nombre d'éléments par page
      const totalPages = Math.ceil(totalResources / resourcesPerPage); // calcul nombre de pages
      setTotalPages(totalPages);
    };

    fetchData();
  }, [currentPage]); // rerender lorsque la page actuelle change

  const handlePageChange = (pageNumber) => { // fonction pour changer la page actuelle passée en props au component pagination
    setCurrentPage(pageNumber);
  };

  return (
    <div className="container mx-auto">
      <table className="my-4">
        <thead>
        {resources.map((resource) => (
  <tr key={resource.id} onClick={() => { window.location.href = `/resource/${resource.id}` }}>
    <td>{resource.id}</td>
    <td>{resource.name}</td>
    <td>{resource.first_brewed}</td>
    <td>{resource.abv}</td>
    <td>{resource.description}</td>
  </tr>
  ))}
        </thead>
        <tbody>
          {resources.map((resource) => ( //map la data données par le fetch pour accéder aux éléments choisis
            <tr key={resource.id}>
              <td>{resource.id}</td>
              <td>
                <a href={`/resource/${resource.id}`}>{resource.name}</a>
              </td>
              <td>{resource.first_brewed}</td>
              <td>{resource.abv}</td>
              <td>{resource.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="mt-4 mb-4 flex justify-center items-center">
        <Pagination //component pagination auquel je passe 3 props
          currentPage={currentPage}
          totalPages={totalPages}
          handlePageChange={handlePageChange}
        />
      </div>
    </div>
  );
};

export default ResourceList;
