import React, { useState, useEffect } from 'react';
import PunkAPIService from '../services/punkapi';
import Pagination from './Pagination';
import NextButton from './NextButton';
import PreviousButton from './PreviousButton';
import './ResourceList.css';

const ResourceList = () => {
  const [resources, setResources] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      const fetchedResources = await PunkAPIService.fetchResources(currentPage);
      setResources(fetchedResources);
      const totalResources = 325; // Total number of beers
      const resourcesPerPage = 20; // Number of resources fetched per page
      const totalPages = Math.ceil(totalResources / resourcesPerPage);
      setTotalPages(totalPages);
    };

    fetchData();
  }, [currentPage]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleNextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const handlePreviousPage = () => {
    setCurrentPage(currentPage - 1);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>First Brewed</th>
            <th>ABV</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {resources.map(resource => (
            <tr key={resource.id}>
              <td>{resource.id}</td>
              <td><a href={`/resource/${resource.id}`}>{resource.name}</a></td>
              <td>{resource.first_brewed}</td>
              <td>{resource.abv}</td>
              <td>{resource.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        {currentPage > 1 && <PreviousButton onClick={handlePreviousPage} />}
        {currentPage < totalPages && <NextButton onClick={handleNextPage} />}
      </div>
    </div>
  );
};

export default ResourceList;
