import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import PunkAPIService from "../services/punkapi";
import axios from "axios";

const ResourceDetail = () => {
  const { id } = useParams();
  const [resource, setResource] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const resource = await PunkAPIService.fetchResource(id);
      setResource(resource);
      console.log (resource);
    };

    fetchData();
  }, [id]);

  const handleSave = () => {
    // Simulate a POST request with axios here
    // As there's no real endpoint, it will return an error
    axios
      .post("/fakeApiEndpoint", { id: resource.id, name: resource.name })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.error("There was an error!", error);
      });
  };

  if (!resource) {
    return <div>Loading...</div>;
  }

  return (
    <div className="flex justify-center items-center min-h-screen p-4">
      <div className="flex flex-col lg:flex-row bg-yellow-200 shadow-xl rounded-lg p-6">
        <figure className="lg:w-1/2"><img src={resource.image_url} alt={resource.name} className="w-full h-60 object-contain"/></figure>
        <div className="card-body lg:w-1/2">
          <h2 className="card-title">{resource.name} {resource.tagline}</h2>
          <p>{resource.description}</p>
          <p><strong>ABV:</strong> {resource.abv}</p>
          <p><strong>Volume:</strong> {resource.volume.value} {resource.volume.unit}</p>
          <p><strong>Ingredients:</strong></p>
          <ul>
            {resource.ingredients.malt.map((malt, index) => (
              <li key={index}>{malt.name}: {malt.amount.value} {malt.amount.unit}</li>
            ))}
            {resource.ingredients.hops.map((hop, index) => (
              <li key={index}>{hop.name}: {hop.amount.value} {hop.amount.unit}</li>
            ))}
          </ul>
          <p><strong>Brewer's Tips:</strong> {resource.brewers_tips}</p>
          <div className="card-actions justify-end">
            <button onClick={handleSave} className="btn btn-primary">Save</button>
          </div>
        </div>
      </div>
    </div>
  );
  

  
  
  
  
};

export default ResourceDetail;
