import React from 'react';
import NextButton from './NextButton';
import PreviousButton from './PreviousButton';

const Pagination = ({ currentPage, handlePageChange, totalPages }) => {
  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          className={currentPage === i ? 'active' : ''}
          className=" px-1 mx-1 rounded bg-blue-600 text-white"
        >
          {i}
        </button>
      );
    }
    return pageNumbers;
  };

  return (
    <div>
      {currentPage > 1 && (
        <PreviousButton onClick={() => handlePageChange(currentPage - 1)} />
      )}
      {renderPageNumbers()}
      <NextButton onClick={() => handlePageChange(currentPage + 1)} />
    </div>
  );
};

export default Pagination;
