import React, { useState, useEffect } from "react";
import PunkAPIService from "../services/punkapi";
import Pagination from "./Pagination";
import SortList from "./SortList";
import Search from "./Search";
import "./ResourceList.css";

const ResourceList = () => {
  const [resources, setResources] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [sortOption, setSortOption] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredResources, setFilteredResources] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const fetchedResources = await PunkAPIService.fetchResources(currentPage);
      setResources(fetchedResources);
      setFilteredResources(fetchedResources); // Initialize filteredResources with all resources
      const totalResources = 325;
      const resourcesPerPage = 20;
      const totalPages = Math.ceil(totalResources / resourcesPerPage);
      setTotalPages(totalPages);
    };

    fetchData();
  }, [currentPage]);

  useEffect(() => {
    const applyFilters = () => {
      const filtered = resources.filter((resource) =>
        resource.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredResources(filtered);
    };

    applyFilters();
  }, [resources, searchTerm]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleSort = (option) => {
    setSortOption(option);

    const sortedResources = [...filteredResources].sort((a, b) => {
      if (option === "abv") {
        return a.abv - b.abv;
      } else if (option === "name") {
        return a.name.localeCompare(b.name);
      } else if (option === "first_brewed") {
        return a.first_brewed.localeCompare(b.first_brewed);
      }
      return 0;
    });

    setFilteredResources(sortedResources);
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  return (
    <div className="container mx-auto">
    <div className="flex justify-between">
      <SortList handleSort={handleSort} />
      <Search handleSearch={handleSearch} />
    </div>
      <table className="my-4">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>First Brewed</th>
            <th>ABV</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {filteredResources.map((resource) => (
            <tr
              key={resource.id}
              onClick={() => {
                window.location.href = `/resource/${resource.id}`;
              }}
              style={{ cursor: "pointer" }}
            >
              <td>{resource.id}</td>
              <td>{resource.name}</td>
              <td>{resource.first_brewed}</td>
              <td>{resource.abv}</td>
              <td>{resource.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="mt-4 mb-4 flex justify-center items-center">
        <Pagination
          currentPage={currentPage}
          totalPages={totalPages}
          handlePageChange={handlePageChange}
        />
      </div>
    </div>
  );
};

export default ResourceList;
